CREATE OR REPLACE FUNCTION obter_ocorrencia_tit_escrit ( nr_titulo_p text, ie_pagar_receber_p text, ie_envio_retorno_p text) RETURNS varchar AS $body$
DECLARE


dt_remessa_ret_w	timestamp;
ds_retorno_w		varchar(255)	:= null;


BEGIN

if (ie_pagar_receber_p	= 'R') and (ie_envio_retorno_p	= 'R') then

	select	max(dt_remessa_retorno)
	into STRICT	dt_remessa_ret_w
	from	cobranca_escritural b,
		titulo_receber_cobr	a
	where	a.nr_seq_cobranca	= b.nr_sequencia
	and	a.nr_titulo 		= nr_titulo_p;

	if (dt_remessa_ret_w IS NOT NULL AND dt_remessa_ret_w::text <> '') then
		select	max(a.ds_ocorrencia)
		into STRICT	ds_retorno_w
		from	cobranca_escritural c,
			titulo_receber_cobr b,
			banco_ocorr_escrit_ret a
		where	b.nr_seq_cobranca	= c.nr_sequencia
		and	b.nr_titulo		= nr_titulo_p
		and	a.nr_sequencia		= b.nr_seq_ocorrencia_ret
		and	c.dt_remessa_retorno	= dt_remessa_ret_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
