CREATE OR REPLACE FUNCTION obter_oc_material ( cd_material_p bigint, cd_estabelecimento_p bigint, dt_inicial_p timestamp, dt_final_p timestamp) RETURNS bigint AS $body$
DECLARE




ds_retorno_w		bigint;




BEGIN



if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	select  max(a.nr_ordem_compra)
	into STRICT 	ds_retorno_w
	from    ordem_compra a,
		ordem_compra_item b
	where   a.nr_ordem_compra = b.nr_ordem_compra
	and     b.cd_material = cd_material_p
	and	coalesce(a.nr_seq_motivo_cancel::text, '') = ''
	and	a.cd_estabelecimento = cd_estabelecimento_p
	and	dt_ordem_compra between dt_inicial_p and fim_dia(dt_final_p);



end if;



RETURN ds_retorno_w;



END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
