CREATE OR REPLACE FUNCTION obter_operacao_tributo ( nr_sequencia_p bigint, cd_tributo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);


BEGIN

select	max(coalesce(ie_retencao,'P'))
into STRICT	ds_retorno_w
from	regra_calculo_imposto
where	nr_sequencia = nr_sequencia_p;

if (ds_retorno_w = 'P') then

	select	max(coalesce(ie_soma_diminui,'N'))
	into STRICT	ds_retorno_w
	from	tributo
	where	cd_tributo = cd_tributo_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
