CREATE OR REPLACE FUNCTION obter_ordem_checagem ( nr_prescricao_p bigint) RETURNS bigint AS $body$
DECLARE


nr_ordem_w	bigint;


BEGIN

select     	min(a.nr_sequencia)
into STRICT	nr_ordem_w
from       	can_ordem_prod a,
           	prescr_medica b,
           	prescr_material c
where      	a.nr_prescricao = b.nr_prescricao
and        	a.nr_sequencia  = c.nr_seq_ordem_prod
and         a.nr_prescricao = nr_prescricao_p
and         a.ie_cancelada  = 'N'
and         (a.dt_fim_preparo IS NOT NULL AND a.dt_fim_preparo::text <> '')
and         coalesce(c.nr_sequencia_diluicao::text, '') = ''
and         coalesce(a.dt_checagem::text, '') = ''
and         ((c.ie_se_necessario = 'N') or (c.ie_urgencia = 'S'))
order by c.nr_agrupamento desc;


return	nr_ordem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_ordem_checagem ( nr_prescricao_p bigint) FROM PUBLIC;

