CREATE OR REPLACE FUNCTION obter_ordem_compra_presc ( nr_prescricao_p bigint, qt_material_p bigint, nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE

 
nr_ordem_compra_w 		bigint;
cd_fornec_consignado_w		varchar(14);
cd_motivo_baixa_w		smallint;


BEGIN 
 
select 	max(cd_fornec_consignado), 
	coalesce(max(cd_motivo_baixa),0) 
into STRICT	cd_fornec_consignado_w, 
	cd_motivo_baixa_w 
from 	prescr_material 
where	nr_prescricao 	= nr_prescricao_p 
and	nr_sequencia 	= nr_sequencia_p;
 
 
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (coalesce(qt_material_p,0) > 0) and (cd_motivo_baixa_w <> 0) then 
	begin 
 
	select	max(b.nr_ordem_compra) 
	into STRICT	nr_ordem_compra_w 
	from	ordem_compra_item a, 
		ordem_compra b 
	where	a.nr_ordem_compra = b.nr_ordem_compra 
	and	b.nr_prescricao = nr_prescricao_p 
	and	b.cd_cgc_fornecedor = cd_fornec_consignado_w 
	and	a.nr_seq_prescr_item = nr_sequencia_p;
 
	/*select 	max(nr_ordem_compra) 
	into	nr_ordem_compra_w 
	from 	ordem_compra 
	where 	nr_prescricao = nr_prescricao_p 
	and 	cd_cgc_fornecedor = cd_fornec_consignado_w;*/
 
 
	end;
end if;
 
return	nr_ordem_compra_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
