CREATE OR REPLACE FUNCTION obter_ordem_iniciada_usuario ( nm_usuario_p text, nr_seq_ativ_prev_p bigint, nr_seq_ordem_p bigint) RETURNS varchar AS $body$
DECLARE


nr_Seq_ordem_w		bigint;


BEGIN

if (coalesce(nr_seq_ativ_prev_p,0) > 0) then

	select	max(NR_SEQ_ORDEM_SERV)
	into STRICT	nr_Seq_ordem_w
	from	man_ordem_serv_ativ a
	where	nr_seq_ordem_serv	<> nr_seq_ordem_p
	and	coalesce(dt_fim_atividade::text, '') = ''
	and	nm_usuario_exec		= nm_usuario_p
	and	dt_atividade between trunc(clock_timestamp()) and fim_dia(clock_timestamp());

end if;

return	nr_Seq_ordem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
