CREATE OR REPLACE FUNCTION obter_ordem_prescricao ( nr_prescricao_p bigint, cd_setor_atendimento_p bigint, cd_tipo_proc_inicial_p bigint, cd_tipo_proc_final_p bigint) RETURNS bigint AS $body$
DECLARE


nr_prescricao_w			bigint;
cd_pessoa_fisica_w		varchar(10);
qt_posicao_w			integer;

C01 CURSOR FOR
SELECT a.nr_prescricao
from	prescr_medica a
where a.cd_pessoa_fisica	= cd_pessoa_fisica_w
  and exists (select 1
	from	Procedimento c,
		procedimento_paciente b
	where a.nr_prescricao		= b.nr_prescricao
	  and b.cd_procedimento		= c.cd_procedimento
	  and	b.ie_origem_proced	= c.ie_origem_proced
	  and	c.cd_tipo_procedimento between
		coalesce(cd_tipo_proc_inicial_p,0) and
		coalesce(cd_tipo_proc_final_p,9999))
order by a.dt_prescricao desc;


BEGIN

select cd_pessoa_fisica
into STRICT	cd_pessoa_fisica_w
from	prescr_medica
where	nr_prescricao	= nr_prescricao_p;

qt_posicao_w		:= 0;
OPEN C01;
LOOP
FETCH C01 into	nr_prescricao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	qt_posicao_w	:= qt_posicao_w + 1;
	if (nr_prescricao_w	= nr_prescricao_p) then
		exit;
	end if;
END LOOP;
close c01;

RETURN qt_posicao_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
