CREATE OR REPLACE FUNCTION obter_ordem_serv_principal (nr_sequencia_p latam_requisito.nr_sequencia%type) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w   latam_requisito.nr_sequencia%type;


BEGIN

    if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '')then
           select max(b.nr_seq_ordem_serv)
           into STRICT nr_sequencia_w
           from latam_requisito a inner join
           latam_requisito_os b on a.nr_sequencia = b.nr_seq_requisito
           where b.ie_tipo_os = '1'
           and a.nr_sequencia = nr_sequencia_p;
    end if;
return	nr_sequencia_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
