CREATE OR REPLACE FUNCTION obter_ordens_compra_cot (nr_cot_compra_p bigint, cd_cgc_fornecedor_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_ordem_compra_w	cot_compra_forn_item.nr_ordem_compra%type;

c01 CURSOR FOR
SELECT  distinct b.nr_ordem_compra
from 	cot_compra_forn_item b,
	cot_compra_forn a
where	b.nr_seq_cot_forn = a.nr_sequencia
and	b.nr_cot_compra = nr_cot_compra_p
and	(b.nr_ordem_compra IS NOT NULL AND b.nr_ordem_compra::text <> '')
and	a.cd_cgc_fornecedor = cd_cgc_fornecedor_p;


BEGIN


open c01;
loop
fetch c01 into
	nr_ordem_compra_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := nr_ordem_compra_w;
	else
		ds_retorno_w := substr(ds_retorno_w||', '||nr_ordem_compra_w,0,255);
	end if;

	end;
end loop;
close c01;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
