CREATE OR REPLACE FUNCTION obter_ordens_gpi ( nr_sequencia_w bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN
select	substr(obter_select_concatenado_bv(
			'select	a.nr_sequencia
			 from	man_ordem_servico_v a,
				gpi_cron_etapa b
			 where	a.nr_seq_etapa_gpi	= b.nr_sequencia
			 and 	b.nr_sequencia = :nr_sequencia',
			'nr_sequencia='||nr_sequencia_w,' ,'),1,255)
into STRICT	ds_retorno_w
;

return	ds_retorno_w;

end;
