CREATE OR REPLACE FUNCTION obter_order_type_batch (nr_atendimento_p bigint, CD_SEQ_PEDIDO_TYPE_P text) RETURNS varchar AS $body$
DECLARE

NR_SEQ_CPOE_TIPO_PEDIDO_W         varchar(255);
CD_SEQ_PEDIDO_TYPE_W              varchar(255);
DS_SEQ_PEDIDO_TYPE_W              varchar(255);

BEGIN
  NR_SEQ_CPOE_TIPO_PEDIDO_W := null;
  CD_SEQ_PEDIDO_TYPE_W      := 0;
  if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
      select (max(coalesce(NR_SEQ_CPOE_TIPO_PEDIDO,0)))
      into STRICT   NR_SEQ_CPOE_TIPO_PEDIDO_W
      from   cpoe_order_unit
      where  nr_atendimento = nr_atendimento_p;
  end if;

 if (CD_SEQ_PEDIDO_TYPE_P = 'CD') then
    CD_SEQ_PEDIDO_TYPE_W := NR_SEQ_CPOE_TIPO_PEDIDO_W;
 elsif (CD_SEQ_PEDIDO_TYPE_P = 'DS') then
    if (coalesce(NR_SEQ_CPOE_TIPO_PEDIDO_W::text, '') = '') then
      CD_SEQ_PEDIDO_TYPE_W := null;
    else
      select DS_TIPO into STRICT CD_SEQ_PEDIDO_TYPE_W from CPOE_TIPO_PEDIDO where NR_SEQUENCIA = NR_SEQ_CPOE_TIPO_PEDIDO_W;
    end if;
 end if;

  RETURN CD_SEQ_PEDIDO_TYPE_W;
END;
$body$
