SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_order_type_cpoe (nr_seq_cpoe_p bigint, ie_tipo_item_p text) RETURNS bigint AS $body$
DECLARE


ds_retorno_w	cpoe_order_unit.nr_sequencia%type;


BEGIN

if (ie_tipo_item_p in ('P', 'E', 'L')) then -- exams and procedures
	select	max(a.nr_seq_cpoe_tipo_pedido)
	into STRICT		ds_retorno_w
	from		cpoe_order_unit a,
				cpoe_procedimento b
	where	b.nr_seq_cpoe_order_unit = a.nr_sequencia
	and		b.nr_sequencia = nr_seq_cpoe_p;
	
	if (ie_tipo_item_p = 'L' and coalesce(ds_retorno_w::text, '') = '') then
		select	max(a.nr_seq_cpoe_tipo_pedido)
	into STRICT		ds_retorno_w
	from		cpoe_order_unit a,
				cpoe_anatomia_patologica b
	where	b.nr_seq_cpoe_order_unit = a.nr_sequencia
	and		b.nr_sequencia = nr_seq_cpoe_p;
	end if;

elsif (ie_tipo_item_p in ('M', 'SOL')) then -- medicines and infusions
	select	max(a.nr_seq_cpoe_tipo_pedido)
	into STRICT		ds_retorno_w
	from		cpoe_order_unit a,
				cpoe_material b
	where	b.nr_seq_cpoe_order_unit = a.nr_sequencia
	and		b.nr_sequencia = nr_seq_cpoe_p;

elsif (ie_tipo_item_p in ('MAT', 'MA')) then -- materials
	select	max(a.nr_seq_cpoe_tipo_pedido)
	into STRICT		ds_retorno_w
	from		cpoe_order_unit a,
				cpoe_material b
	where	b.nr_seq_cpoe_order_unit = a.nr_sequencia
	and		b.nr_sequencia = nr_seq_cpoe_p;

elsif (ie_tipo_item_p in ('D', 'SNE', 'S', 'LD', 'NPN', 'NPP', 'J', 'NPTA', 'NPTI', 'NAN')) then -- nutrition
	select	max(a.nr_seq_cpoe_tipo_pedido)
	into STRICT		ds_retorno_w
	from		cpoe_order_unit a,
				cpoe_dieta b
	where	b.nr_seq_cpoe_order_unit = a.nr_sequencia
	and		b.nr_sequencia = nr_seq_cpoe_p;

elsif (ie_tipo_item_p = 'R') then -- interventions and recommendations
	select	max(a.nr_seq_cpoe_tipo_pedido)
	into STRICT		ds_retorno_w
	from		cpoe_order_unit a,
				cpoe_recomendacao b
	where	b.nr_seq_cpoe_order_unit = a.nr_sequencia
	and		b.nr_sequencia = nr_seq_cpoe_p;

elsif (ie_tipo_item_p = 'DI') then -- dialisys
	select	max(a.nr_seq_cpoe_tipo_pedido)
	into STRICT		ds_retorno_w
	from		cpoe_order_unit a,
				cpoe_dialise b
	where	b.nr_seq_cpoe_order_unit = a.nr_sequencia
	and		b.nr_sequencia = nr_seq_cpoe_p;


elsif (ie_tipo_item_p in ('O', 'G')) then -- gas therapy
	select	max(a.nr_seq_cpoe_tipo_pedido)
	into STRICT		ds_retorno_w
	from		cpoe_order_unit a,
				cpoe_gasoterapia b
	where	b.nr_seq_cpoe_order_unit = a.nr_sequencia
	and		b.nr_sequencia = nr_seq_cpoe_p;

elsif (ie_tipo_item_p in ('HM')) then

	select	max(a.nr_seq_cpoe_tipo_pedido)
	into STRICT		ds_retorno_w
	from		cpoe_order_unit a,
				cpoe_hemoterapia b
	where	b.nr_seq_cpoe_order_unit = a.nr_sequencia
	and		b.nr_sequencia = nr_seq_cpoe_p;

end if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
