CREATE OR REPLACE FUNCTION obter_orgao_cobranca_atual (nr_seq_cobranca_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_orgao_w	bigint;


BEGIN

select 	max(nr_seq_orgao)
into STRICT	nr_seq_orgao_w
from	(SELECT		a.nr_seq_orgao
		from		cobranca_orgao a
		where		a.nr_seq_cobranca = nr_seq_cobranca_p
		and			a.nr_sequencia = (	select max(x.nr_sequencia) /*aamfirmo OS 1081326, tinha orgão sem dt inclusao que não era o ultimo, ai trazia o orgao errado. Assim vai trazer sempre o ultimo, o de maior sequencia*/
										from	cobranca_orgao x
										where	x.nr_seq_cobranca = a.nr_seq_cobranca)
	order by	a.dt_inclusao desc,
			a.nr_sequencia desc) alias3 LIMIT 1;

return	nr_seq_orgao_w;

end;
