SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_orientacao_alta_pend ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(2) := 'N';
qt_registro_w		bigint := 0;
dt_alta_w		timestamp;


BEGIN 
 
select	max(dt_alta) 
into STRICT	dt_alta_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
 
if (coalesce(dt_alta_w::text, '') = '') then 
 
	select count(*) 
	into STRICT	qt_registro_w	 
	from  atendimento_anot_enf a 
	where  a.nr_atendimento = nr_atendimento_p 
	and	a.ie_pendente = 'S' 
	and	coalesce(a.dt_inativacao::text, '') = '';
 
end if;
	 
if ( qt_registro_w > 0 ) then 
	ds_retorno_w := 'S';
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
