SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_orientacao_dialise ( nr_seq_dialise_cpoe_p bigint, nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_orientacao_w		cpoe_dialise.ds_orientacao%type;
ds_observacao_w		cpoe_dialise.ds_observacao%type;
ds_retorno_w		varchar(1000);


BEGIN

if (nr_seq_dialise_cpoe_p IS NOT NULL AND nr_seq_dialise_cpoe_p::text <> '') then

	select	max(a.ds_orientacao),
			max(a.ds_observacao)
	into STRICT	ds_orientacao_w,
			ds_observacao_w
	from	cpoe_dialise a
	where	a.nr_sequencia = nr_seq_dialise_cpoe_p;
	
	if (ds_orientacao_w IS NOT NULL AND ds_orientacao_w::text <> '') then
	
		ds_retorno_w := substr(wheb_mensagem_pck.get_texto(857391,'ds_orientacao='||ds_orientacao_w) || chr(10), 1, 1000);
	
	end if;
	
	if (ds_observacao_w IS NOT NULL AND ds_observacao_w::text <> '') then
	
		ds_retorno_w := substr(ds_retorno_w || obter_desc_expressao(294678)||': '||ds_observacao_w, 1, 1000);
	
	end if;

elsif (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '' AND nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	max(a.ds_observacao)
	into STRICT	ds_observacao_w
	from	hd_prescricao a
	where	a.nr_prescricao = nr_prescricao_p
	and		a.nr_sequencia = nr_sequencia_p;
	
	if (ds_observacao_w IS NOT NULL AND ds_observacao_w::text <> '') then
	
		ds_retorno_w := substr(wheb_mensagem_pck.get_texto(857391,'ds_orientacao='||ds_observacao_w), 1, 1000);
	
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
