CREATE OR REPLACE FUNCTION obter_orientacao_dieta (cd_dieta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(32767);


BEGIN
if (cd_dieta_p IS NOT NULL AND cd_dieta_p::text <> '') then
	begin
	select	ds_orientacao
	into STRICT	ds_retorno_w
	from	dieta_orientacao
	where	cd_dieta = cd_dieta_p;
	exception
		when others then
		ds_retorno_w := '';
	end;
end if;

return substr(ds_retorno_w,1,4000);

end;
