CREATE OR REPLACE FUNCTION obter_orientacao_medic_pac (nr_seq_entrega_item_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000);
nr_seq_rec_item_w	bigint;


BEGIN

select 	t.nr_sequencia
into STRICT	nr_seq_rec_item_w
from   	fa_entrega_medicacao_item i,
	fa_entrega_medicacao m,
	fa_receita_farmacia f,
	fa_receita_farmacia_item t
where 	i.nr_seq_fa_entrega = m.nr_sequencia
and   	f.nr_sequencia = m.nr_seq_receita_amb
and   	t.nr_seq_receita = f.nr_sequencia
and   	i.nr_sequencia = nr_seq_entrega_item_p
and   	t.cd_material = cd_material_p;


select	ds_orientacao
into STRICT	ds_retorno_w
from 	fa_receita_far_orient_item
where	nr_seq_receita_item =  nr_seq_rec_item_w;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
