CREATE OR REPLACE FUNCTION obter_orientacao_proced_pac (cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_proc_interno_p bigint) RETURNS varchar AS $body$
DECLARE


ds_orientacao_w		varchar(4000)	:= '';
ds_orient_usuario_w	varchar(4000)	:= '';
ds_orient_paciente_w	varchar(4000)  := '';
ie_orientacao_w		varchar(01);

expressao1_w	varchar(255) := obter_desc_expressao_idioma(294909, null, wheb_usuario_pck.get_nr_seq_idioma);--Orientação paciente
BEGIN

select	max(ds_orientacao)
into STRICT	ds_orientacao_w
from	procedimento
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;

if (coalesce(nr_seq_proc_interno_p, 0) > 0) then
	begin

	select	max(ie_orientacao),
		max(ds_orientacao_usuario),
		max(ds_orientacao_pac)
	into STRICT	ie_orientacao_w,
		ds_orient_usuario_w,
		ds_orient_paciente_w
	from	proc_interno
	where	nr_sequencia	= nr_seq_proc_interno_p;

	if (ie_orientacao_w = 'A') then
		ds_orientacao_w	:= substr(expressao1_w || ': ' || ds_orient_paciente_w,1,4000);
	end if;

	end;
end if;

return	ds_orientacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
