CREATE OR REPLACE FUNCTION obter_orient_exame_agexame (cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_convenio_p bigint, nr_seq_proc_interno_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, cd_funcao_p bigint, nr_seq_tipo_classif_pac_p bigint, nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ds_orientacao_w	varchar(4000) := '';
ds_orient_convenio_w	varchar(2000) := '';
ds_orientacao_usuario_w	varchar(4000) := '';
ie_orientacao_w		varchar(01);
ie_tipo_orientacao_w	varchar(02);
nr_seq_classif_w	bigint;
qt_idade_w		bigint;
ds_orient_paciente_w	varchar(4000) := '';
cd_medico_exec_w	varchar(10);

C02 CURSOR FOR
	SELECT	ds_orientacao_pac
	from	proc_interno_pac_orient
	where	coalesce(nr_seq_proc_interno,coalesce(nr_seq_proc_interno_p,0)) = coalesce(nr_seq_proc_interno_p,0)
	and	coalesce(ie_situacao,'A') = 'A'
	and	qt_idade_w between coalesce(qt_idade_min, qt_idade_w) and coalesce(qt_idade_max, qt_idade_w)
	and (coalesce(nr_seq_classif, coalesce(nr_seq_classif_w,0)) = coalesce(nr_seq_classif_w,0))
	and (coalesce(nr_seq_tipo_classif_pac, coalesce(nr_seq_tipo_classif_pac_p,0)) = coalesce(nr_seq_tipo_classif_pac_p,0))
	and (coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_p,1)) = coalesce(cd_estabelecimento_p,1))
	and	((cd_pessoa_fisica = cd_medico_exec_w) or (coalesce(cd_pessoa_fisica::text, '') = ''))
	order by	coalesce(nr_seq_proc_interno,0),
		coalesce(nr_seq_classif,0);


BEGIN

select	max(ds_orientacao)
into STRICT	ds_orientacao_w
from	procedimento
where	cd_procedimento = cd_procedimento_p
and	ie_origem_proced = ie_origem_proced_p;


ie_tipo_orientacao_w := Obter_Param_Usuario(820, 67, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_tipo_orientacao_w);

if (coalesce(nr_seq_proc_interno_p,0) > 0) then

	select	coalesce(max(qt_idade_paciente),0),
		max(cd_medico_exec)
	into STRICT	qt_idade_w,
		cd_medico_exec_w
	from	agenda_paciente
	where	nr_sequencia	= nr_seq_agenda_p;

	select	max(ie_orientacao),
		max(ds_orientacao_pac),
		max(ds_orientacao_usuario),
		coalesce(max(nr_seq_classif),0)
	into STRICT	ie_orientacao_w,
		ds_orientacao_w,
		ds_orientacao_usuario_w,
		nr_seq_classif_w
	from	proc_interno
	where	nr_sequencia = nr_seq_proc_interno_p;


	select	max(ds_orientacao)
	into STRICT	ds_orient_convenio_w
	from	proc_interno_conv_orient
	where	nr_seq_proc_interno = nr_seq_proc_interno_p
	and	((cd_convenio = cd_convenio_p) or (coalesce(cd_convenio::text, '') = ''));

	open C02;
	loop
	fetch C02 into
		ds_orient_paciente_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		ds_orient_paciente_w := ds_orient_paciente_w;
		end;
	end loop;
	close C02;

	--(-20011,cd_funcao_w || '#@#@');
	if (ie_orientacao_w = 'C') then
		ds_orientacao_w := substr(ds_orient_convenio_w,1,4000);
	elsif (ie_orientacao_w = 'A') and ((ie_tipo_orientacao_w = 'P') or (cd_funcao_p <> 820)) then
		ds_orientacao_w := substr(ds_orientacao_w || chr(13) || chr(10) || ds_orient_convenio_w ||  chr(13) || chr(10) || ds_orient_paciente_w,1,4000);
	elsif (ie_orientacao_w = 'A') and (ie_tipo_orientacao_w = 'U') and (cd_funcao_p = 820) then
		ds_orientacao_w := substr(ds_orientacao_usuario_w || chr(13) || chr(10) || ds_orient_convenio_w,1,4000);
	elsif (ie_tipo_orientacao_w = 'U') and (cd_funcao_p = 820) then
		ds_orientacao_w := substr(ds_orientacao_usuario_w,1,4000);
	elsif (ie_orientacao_w = 'A') and (ie_tipo_orientacao_w = 'PU') and (cd_funcao_p = 820) then
		ds_orientacao_w := substr(ds_orientacao_w || chr(13) || chr(10) || ds_orient_convenio_w || chr(13) || chr(10) ||ds_orientacao_usuario_w ||  chr(13) || chr(10) || ds_orient_paciente_w,1,4000);
	elsif (ie_tipo_orientacao_w = 'PU') and (cd_funcao_p = 820) then
		ds_orientacao_w := substr(ds_orientacao_w || chr(13) || chr(10) || ds_orientacao_usuario_w ||  chr(13) || chr(10) || ds_orient_paciente_w,1,4000);
	end if;
end if;

return ds_orientacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
