CREATE OR REPLACE FUNCTION obter_orient_proc_tipo_classif ( nr_seq_procedimento_p bigint, nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

			 
ds_retorno_w			varchar(4000);
ds_retorno_concat_w		varchar(8000);

C01 CURSOR FOR 
	SELECT 	coalesce(d.ds_orientacao_pac, '')	 
	from  	agenda_paciente a, 
		proc_interno b, 
		agenda c, 
		proc_interno_pac_orient d 
	where 	a.nr_seq_proc_interno = b.nr_sequencia 
	and	a.cd_agenda = c.cd_agenda 
	and	b.nr_sequencia = d.nr_seq_proc_interno 
	and	a.nr_seq_tipo_classif_pac = d.nr_seq_tipo_classif_pac 
	and	b.nr_sequencia = nr_seq_procedimento_p 
	and	a.nr_sequencia = nr_seq_agenda_p 
	and	c.cd_tipo_agenda = '2';


BEGIN 
ds_retorno_w	:= '';
 
if (nr_seq_procedimento_p IS NOT NULL AND nr_seq_procedimento_p::text <> '') and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then 
	begin 
 
	open C01;
	loop 
	fetch C01 into	 
		ds_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		 
		if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') or (ds_retorno_w <> '')then 
			ds_retorno_concat_w := ds_retorno_concat_w||' '||ds_retorno_w;
		 
		end if;
		 
		end;
	end loop;
	close C01;
	 
	if (coalesce(ds_retorno_w::text, '') = '') or (ds_retorno_w = '') then  
		begin 
		select 	max(b.ds_orientacao_usuario) 
		into STRICT	ds_retorno_concat_w 
		from  	agenda_paciente a, 
			proc_interno b, 
			agenda c 
		where 	a.nr_seq_proc_interno = b.nr_sequencia 
		and	a.cd_agenda = c.cd_agenda 
		and	a.nr_seq_proc_interno = nr_seq_procedimento_p 
		and	a.nr_sequencia = nr_seq_agenda_p 
		and	c.cd_tipo_agenda = '2';
		end;
	 
	end if;
	 
	exception 
	when others then 
		ds_retorno_concat_w := '';
	 
	end;
end if;
 
return	ds_retorno_concat_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
