CREATE OR REPLACE FUNCTION obter_origem_convenio (cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE


ds_origem_convenio_w   varchar(100);


BEGIN
	if (cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '') then
		select       max(substr(ds_origem, 1, 100))
		into STRICT	     ds_origem_convenio_w
		from         convenio_origem_usuario
		where        cd_convenio = cd_convenio_p
		and          coalesce(ie_situacao,'A') = 'A';
	else
	ds_origem_convenio_w := wheb_mensagem_pck.get_texto(793287);
	end if;

return	ds_origem_convenio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
