CREATE OR REPLACE FUNCTION obter_origem_conv_atend (nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_origem_w		bigint;
ds_retorno_w		varchar(60);

/*
	ie_opcao_p	:	'C' - Código
				'D' - Descrição		*/
BEGIN

select	max(coalesce(nr_seq_origem,0))
into STRICT	nr_seq_origem_w
from	atend_categoria_convenio
where	nr_atendimento	= nr_atendimento_p;

if (nr_seq_origem_w <> 0) then

	if (upper(ie_opcao_p) = 'C') then
		ds_retorno_w	:= substr(nr_seq_origem_w,1,10);

	elsif (upper(ie_opcao_p) = 'D') then
		select	ds_origem
		into STRICT	ds_retorno_w
		from	convenio_origem_usuario
		where	nr_sequencia	= nr_seq_origem_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
