CREATE OR REPLACE FUNCTION obter_origem_gastos ( nr_cirurgia_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_origem_gasto_w		varchar(255);

C01 CURSOR FOR
SELECT 	distinct(obter_valor_dominio(1815,ie_origem_gasto)) ds_origem_gasto
from   	cirurgia_agente_disp
where  	ie_operacao = 'D'
and	nr_cirurgia = nr_cirurgia_p
and	cd_material = cd_material_p
order by obter_valor_dominio(1815,ie_origem_gasto);


BEGIN
ds_retorno_w := '';

open C01;
loop
	fetch C01 into
		ds_origem_gasto_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(ds_retorno_w::text, '') = '') or (ds_retorno_w = '') then
		ds_retorno_w := ds_retorno_w || ds_origem_gasto_w;
	else
		ds_retorno_w := ds_retorno_w || ',' || ds_origem_gasto_w;
	end if;
	end;
end loop;
close C01;


return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
