CREATE OR REPLACE FUNCTION obter_origem_medic_frac ( nr_sequencia_p bigint ) RETURNS varchar AS $body$
DECLARE


nr_seq_origem_medic_w	bigint;


BEGIN

select	max(nr_seq_origem_medic)
into STRICT	nr_seq_origem_medic_w
from	adep_processo_frac a,
	prescr_mat_hor b,
	prescr_material c
where	b.nr_prescricao	  	= c.nr_prescricao
and	b.nr_seq_material 	= c.nr_sequencia
and	b.nr_seq_etiqueta 	= a.nr_sequencia
and	a.nr_sequencia		= nr_sequencia_p;
--and	Obter_se_horario_liberado(b.dt_lib_horario, b.dt_horario) = 'S';
return	nr_seq_origem_medic_w;

end;
