SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_origem_processo_aprov (nr_seq_aprovacao_p bigint) RETURNS varchar AS $body$
DECLARE

				
/* Retorno
O - Ordem compra
S - Solicitacao compra
R - Requisicao materiais
C - Cotacao de compra 
N - Nota fiscal
L - Registro licitacao
G - Contrato
E - Projetos e recursos externos*/
ie_ordem_w		varchar(1);
ie_solicitacao_w	varchar(1);
ie_requisicao_w		varchar(1);
vl_retorno_w		varchar(1);
ie_cotacao_w		varchar(1);
ie_nota_fiscal_w	varchar(1);
ie_reg_licitacao_w	varchar(1);
ie_contrato_w		varchar(1);
ie_projeto_w		varchar(1);


BEGIN
select	coalesce(max('S'),'N')
into STRICT	ie_ordem_w
from	ordem_compra_item a,
	processo_aprov_compra b
where	a.nr_seq_aprovacao = b.nr_sequencia
and	b.nr_sequencia = nr_seq_aprovacao_p;

if (ie_ordem_w = 'S') then
	vl_retorno_w := 'O';
else
	
	begin
	select	coalesce(max('S'),'N')
	into STRICT	ie_solicitacao_w
	from	solic_compra_item a,
		processo_aprov_compra b
	where	a.nr_seq_aprovacao = b.nr_sequencia
	and	b.nr_sequencia = nr_seq_aprovacao_p;
	if (ie_solicitacao_w = 'S') then
		vl_retorno_w := 'S';
	else
		begin
		select	coalesce(max('S'),'N')
		into STRICT	ie_requisicao_w
		from	item_requisicao_material a,
			processo_aprov_compra b
		where	a.nr_seq_aprovacao = b.nr_sequencia
		and	b.nr_sequencia = nr_seq_aprovacao_p;
		if (ie_requisicao_w = 'S') then
			vl_retorno_w := 'R';
		else
			begin
			select	coalesce(max('S'),'N')
			into STRICT	ie_cotacao_w
			from	cot_compra_item a,
				processo_aprov_compra b
			where	a.nr_seq_aprovacao = b.nr_sequencia
			and	b.nr_sequencia = nr_seq_aprovacao_p;
			if (ie_cotacao_w = 'S') then
				vl_retorno_w := 'C';
			else
				begin
				select	coalesce(max('S'),'N')
				into STRICT	ie_nota_fiscal_w
				from	nota_fiscal_item a,
					processo_aprov_compra b
				where	a.nr_seq_aprovacao = b.nr_sequencia
				and	b.nr_sequencia = nr_seq_aprovacao_p;
				if (ie_nota_fiscal_w = 'S') then
					vl_retorno_w := 'N';
				else
					begin
					select	coalesce(max('S'),'N')
					into STRICT	ie_reg_licitacao_w
					from	reg_lic_item a,
						processo_aprov_compra b
					where	a.nr_seq_aprovacao = b.nr_sequencia
					and	b.nr_sequencia = nr_seq_aprovacao_p;
					if (ie_reg_licitacao_w = 'S') then
						vl_retorno_w := 'L';
					else
						begin
						select	coalesce(max('S'),'N')
						into STRICT	ie_contrato_w
						from	contrato a,
							processo_aprov_compra b
						where	a.nr_seq_aprovacao = b.nr_sequencia
						and	b.nr_sequencia = nr_seq_aprovacao_p;
						if (ie_contrato_w = 'S') then
							vl_retorno_w := 'G';
						else
							begin
							select	CASE WHEN coalesce(max(a.nr_sequencia)::text, '') = '' THEN 'N'  ELSE 'S' END
							into STRICT	ie_projeto_w
							from	projeto_recurso a,
								processo_aprov_compra b
							where	a.nr_seq_aprovacao = b.nr_sequencia
							and	b.nr_sequencia = nr_seq_aprovacao_p;
							if (ie_projeto_w = 'S') then
								vl_retorno_w := 'E';
							end if;
							end;
						end if;
						end;
					end if;	
					end;
				end if;
				end;
			end if;	
			end;
		end if;
		end;
	end if;
	end;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_origem_processo_aprov (nr_seq_aprovacao_p bigint) FROM PUBLIC;

