CREATE OR REPLACE FUNCTION obter_origem_titulo_receber (nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE


/*
C - Conta;
O - Outros;
*/
nr_seq_protocolo_w	bigint;
nr_interno_conta_w	bigint;
cd_convenio_conta_w	integer;
ds_retorno_w		varchar(1)	:= 'C';


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
	select	coalesce(nr_seq_protocolo,0),
		coalesce(nr_interno_conta,0),
		coalesce(cd_convenio_conta,0)
	into STRICT	nr_seq_protocolo_w,
		nr_interno_conta_w,
		cd_convenio_conta_w
	from	titulo_receber
	where	nr_titulo	= nr_titulo_p;

	if (nr_seq_protocolo_w 	= 0) and (nr_interno_conta_w	= 0) and (cd_convenio_conta_w	= 0) then
		ds_retorno_w	:= 'O';
	end if;
end if;

RETURN	ds_retorno_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
