SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_orig_dest_banco (nr_seq_mov_trans_p bigint) RETURNS varchar AS $body$
DECLARE


nr_bordero_w            bigint;
nr_seq_titulo_pagar_w   bigint;
nr_seq_banco_escrit_w   bigint;
cd_banco_w              bigint;


BEGIN

select  nr_bordero,
        nr_seq_banco_escrit
into STRICT    nr_bordero_w,
        nr_seq_banco_escrit_w
from    movto_trans_financ
where   nr_sequencia = nr_seq_mov_trans_p;

if (nr_seq_banco_escrit_w IS NOT NULL AND nr_seq_banco_escrit_w::text <> '') then

        select  cd_banco
        into STRICT    cd_banco_w
        from    banco_escritural
        where   nr_sequencia = nr_seq_banco_escrit_w;

        return substr(obter_nome_banco(cd_banco_w), 1, 40);

elsif (nr_bordero_w IS NOT NULL AND nr_bordero_w::text <> '') then

  select  max(b.nr_titulo)
  into STRICT    nr_seq_titulo_pagar_w
  from    movto_trans_financ a,
    bordero_tit_pagar b
  where   a.nr_bordero   = b.nr_bordero
  and  a.nr_sequencia   = nr_seq_mov_trans_p;

        return substr(obter_dados_conta_titulo_pagar(nr_seq_titulo_pagar_w, obter_dados_tit_pagar(nr_seq_titulo_pagar_w,'ESTAB'), 'DB'), 1, 40);

end if;

return null;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_orig_dest_banco (nr_seq_mov_trans_p bigint) FROM PUBLIC;

