CREATE OR REPLACE FUNCTION obter_os_funcao_mes ( cd_funcao_p bigint, dt_mes_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_os_w		bigint := 0;
dt_inicio_mes_w	timestamp;
dt_fim_mes_w	timestamp;


BEGIN
if (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') and (dt_mes_p IS NOT NULL AND dt_mes_p::text <> '') then
	begin
	dt_inicio_mes_w	:= inicio_dia(trunc(dt_mes_p,'month'));
	dt_fim_mes_w	:= fim_dia(last_day(dt_mes_p));
	select	count(*)
	into STRICT	qt_os_w
	from	man_ordem_servico
	where	cd_funcao = cd_funcao_p
	and	dt_ordem_servico between dt_inicio_mes_w and dt_fim_mes_w;
	end;
end if;
return qt_os_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
