CREATE OR REPLACE FUNCTION obter_os_pend_gerencia (nr_seq_gerencia_p bigint, dt_mes_referencia_p timestamp, ie_tipo_informacao_p text) RETURNS bigint AS $body$
DECLARE


/* 'T' - Total pendentes		'P' - Por pessoas pessoas */

qt_total_pendente_w		bigint;
qt_pessoas_w			bigint;
dt_fim_mes_w			timestamp;


BEGIN

if (trunc(clock_timestamp(), 'month') = dt_mes_referencia_p) then
	dt_fim_mes_w		 := trunc(clock_timestamp(), 'dd');
else
	dt_fim_mes_w		 := dt_mes_referencia_p;
end if;

select	sum(a.qt_total)
into STRICT	qt_total_pendente_w
from 	man_posicao_diaria b,
	man_posicao_diaria_resumo a
where   a.nr_seq_gerencia       = nr_seq_gerencia_p
and     a.nr_seq_posicao        = b.nr_sequencia
and     b.dt_posicao            = dt_fim_mes_w
and     b.ie_tipo_registro 	= 1
and	coalesce(a.ie_tipo,'P') 	= 'P'
and	coalesce(b.ie_tipo,'P') 	= 'P';

select	Obter_Qt_Colab_Real_mes_des(nr_seq_gerencia_p, trunc(dt_fim_mes_w, 'month'))
into STRICT	qt_pessoas_w
;

if (ie_tipo_informacao_p	= 'T') then
	return	qt_total_pendente_w;
else
	return	qt_total_pendente_w/qt_pessoas_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
