CREATE OR REPLACE FUNCTION obter_os_reg_validation_item ( nr_seq_validation_item_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(400);
  item RECORD;

BEGIN
	ds_retorno_w := '';
	for item in (
		SELECT o.nr_seq_service_order, o.nr_seq_validation_item
		from   reg_validation_so_pend o
		where  o.nr_seq_validation_item = nr_seq_validation_item_p
	)
	loop
		ds_retorno_w := ds_retorno_w || ', ' || item.nr_seq_service_order;
	end loop;

	ds_retorno_w := substr(ds_retorno_w,2,length(ds_retorno_w));

	return  ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
