CREATE OR REPLACE FUNCTION obter_outras_reducoes_irpf (cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_reducao_w	double precision;


BEGIN

select	coalesce(sum(vl_reducao), 0)
into STRICT	vl_reducao_w
from	pessoa_fisica_reducao_irpf
where	cd_pessoa_fisica		= cd_pessoa_fisica_p
and	cd_estabelecimento		= cd_estabelecimento_p
and	trunc(dt_referencia_p, 'dd')	between trunc(dt_inicio_vigencia, 'dd') and dt_fim_vigencia;

return	vl_reducao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
