CREATE OR REPLACE FUNCTION obter_paciente_agendamento ( cd_agenda_p bigint, nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(60);	
cd_tipo_Agenda_w	bigint;		
			 

BEGIN 
 
select	cd_tipo_Agenda 
into STRICT	cd_tipo_Agenda_w 
from	agenda 
where	cd_agenda	= cd_Agenda_p;
 
if (cd_tipo_Agenda_w	= 2) then 
	select	coalesce(substr(obter_nome_pf(cd_pessoa_fisica),1,60),nm_paciente) 
	into STRICT	ds_retorno_w 
	from	agenda_paciente 
	where	nr_sequencia	= nr_seq_agenda_p;
elsif (cd_tipo_Agenda_w	= 3) then 
	select	coalesce(substr(obter_nome_pf(cd_pessoa_fisica),1,60),nm_paciente) 
	into STRICT	ds_retorno_w 
	from	agenda_consulta 
	where	nr_sequencia	= nr_seq_agenda_p;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
