CREATE OR REPLACE FUNCTION obter_paciente_autor_onc (nr_seq_paciente_setor_p bigint) RETURNS varchar AS $body$
DECLARE

 
nm_paciente_w		varchar(255);


BEGIN 
 
if (nr_seq_paciente_setor_p IS NOT NULL AND nr_seq_paciente_setor_p::text <> '') then 
 
	select 	max(obter_nome_pf(b.cd_pessoa_fisica)) 
	into STRICT	nm_paciente_w 
	from	paciente_setor b, 
		paciente_setor_convenio a 
	where	a.nr_seq_paciente	= b.nr_seq_paciente 
	and	a.nr_seq_paciente	= nr_seq_paciente_setor_p;
 
end if;
 
return	nm_paciente_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
