CREATE OR REPLACE FUNCTION obter_paciente_compl_assist ( nr_atendimento_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


	ds_retorno_w varchar(4000);	


BEGIN	

	select	obter_descricao_dominio(8694, coalesce(max(pca.ie_nivel_compl), 0))
	into STRICT	ds_retorno_w
	from	paciente_compl_assist pca
	where	pca.nr_atendimento = nr_atendimento_p
	and	pca.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	(pca.dt_liberacao IS NOT NULL AND pca.dt_liberacao::text <> '')
	and	coalesce(pca.dt_inativacao::text, '') = ''
	and	pca.nr_sequencia = (SELECT	max(x.nr_sequencia)
				from	paciente_compl_assist x
				where	x.nr_atendimento = pca.nr_atendimento
				and	x.cd_pessoa_fisica = pca.cd_pessoa_fisica
				and	(x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
				and	coalesce(x.dt_inativacao::text, '') = '');	
	

return ds_retorno_w;

end;
$body$
