CREATE OR REPLACE FUNCTION obter_paciente_conta ( nr_interno_conta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
 
ds_retorno_w		varchar(60);

 

BEGIN 
 
select	b.cd_pessoa_fisica 
into STRICT	ds_retorno_w 
from	atendimento_paciente	b, 
	conta_paciente		a 
where	a.nr_atendimento	= b.nr_atendimento 
and	a.nr_interno_conta	= nr_interno_conta_p;
 
if (ie_opcao_p = 'D') then 
	ds_retorno_w	:= obter_nome_pf(ds_retorno_w);
end if;
 
return	ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
