CREATE OR REPLACE FUNCTION obter_paciente_cracha ( nr_controle_p text) RETURNS varchar AS $body$
DECLARE


nr_atendimento_w	bigint;


BEGIN

if (nr_controle_p IS NOT NULL AND nr_controle_p::text <> '') then
	select	max(b.nr_atendimento)
	into STRICT	nr_atendimento_w
	from	unidade_atendimento b,
		unidade_atend_cracha a
	where	a.cd_setor_atendimento 	= b.cd_setor_atendimento
	and	a.cd_unidade_basica	= b.cd_unidade_basica
	and	(b.nr_atendimento IS NOT NULL AND b.nr_atendimento::text <> '')
	and	a.cd_unidade_compl	= b.cd_unidade_compl
	and	a.cd_cracha		= nr_controle_p;

end if;

return nr_atendimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
