CREATE OR REPLACE FUNCTION obter_paciente_ficha (nr_ficha_ocorrencia_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_codigo_pessoa_w varchar(10);
nr_atendimento_w bigint;


BEGIN 
 
IF (nr_ficha_ocorrencia_p > 0) THEN 
 
 SELECT MAX(nr_atendimento) 
 INTO STRICT nr_atendimento_w 
 FROM cih_ficha_ocorrencia 
 WHERE nr_ficha_ocorrencia = nr_ficha_ocorrencia_p;
 
 IF (nr_atendimento_w > 0) THEN 
 
 SELECT MAX(cd_pessoa_fisica) 
 INTO STRICT cd_codigo_pessoa_w 
 FROM atendimento_paciente 
 WHERE nr_atendimento = nr_atendimento_w;
 
 END IF;
 
END IF;
 
RETURN cd_codigo_pessoa_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
