CREATE OR REPLACE FUNCTION obter_paciente_gestao_vagas ( nr_seq_gestao_p bigint, ie_codigo_nome_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(60)	:= null;


BEGIN 
 
if (ie_codigo_nome_p = 'C') then 
	begin 
	select	cd_pessoa_fisica 
	into STRICT	ds_retorno_w 
	from	gestao_vaga 
	where	nr_sequencia	=	nr_seq_gestao_p;
	exception 
		when others then 
			ds_retorno_w	:= null;
	end;
elsif (ie_codigo_nome_p = 'N') then 
	begin 
	select	substr(obter_nome_pf(cd_pessoa_fisica),1,60) 
	into STRICT	ds_retorno_w 
	from	gestao_vaga 
	where	nr_sequencia	=	nr_seq_gestao_p;	
	exception 
		when others then		 
		ds_retorno_w	:= null;			
	end;
	 
	if (coalesce(ds_retorno_w::text, '') = '') then 
	 
		select 	substr(nm_paciente,1,60) 
		into STRICT	ds_retorno_w 
		from	gestao_vaga 
		where	nr_sequencia	=	nr_seq_gestao_p;
	 
	end if;	
 
end if;
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
