CREATE OR REPLACE FUNCTION obter_paciente_pre_agen_mut (nr_seq_lista_espera_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);

BEGIN

if (nr_seq_lista_espera_p IS NOT NULL AND nr_seq_lista_espera_p::text <> '')then
	select	coalesce(max('S'), 'N')
	into STRICT 	ds_retorno_w
	from  	agenda_lista_espera a,
			agenda_integrada b,
			agenda_integrada_item c,
			mutirao_lista_espera d
	where 	a.nr_sequencia = b.nr_seq_lista_espera  
	and  	b.nr_sequencia = c.nr_seq_agenda_int
	and   	c.nr_seq_multirao = d.nr_sequencia
	and   	clock_timestamp() between d.dt_inicio and d.dt_termino
	and 	a.ie_status_espera = 'A'
	and   	a.nr_sequencia = nr_seq_lista_espera_p;
end if;

return	coalesce(ds_retorno_w, 'N');

end;
