CREATE OR REPLACE FUNCTION obter_paciente_pront_prot_doc ( nr_prontuario_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	ds_retorno_w := substr(obter_nome_pf(cd_pessoa_fisica_p),1,255);
else
	ds_retorno_w := substr(obter_paciente_prontuario(nr_prontuario_p,'D'),1,255);
end if;

return	ds_retorno_w;

end;
$body$
