CREATE OR REPLACE FUNCTION obter_paciente_restric_sql (ie_restricao_p text, ie_executado_p text DEFAULT NULL, ie_canceladas_p text DEFAULT NULL) RETURNS varchar AS $body$
DECLARE


ds_retorno_w					varchar(4000);
ie_restricao_estab_w	varchar(4000);
ie_restricao_status_W	varchar(4000);
			
  i RECORD;

BEGIN

  if ie_restricao_p = 'E' then
    ie_restricao_estab_w := ' AND a.cd_estabelecimento IN ( ';
    FOR i IN (SELECT x.cd_estabelecimento FROM usuario_estabelecimento_v x WHERE x.nm_usuario_param = obter_usuario_ativo) LOOP
      ie_restricao_estab_w := ie_restricao_estab_w || i.cd_estabelecimento || ',';
    END LOOP;
    ds_retorno_w  := ie_restricao_estab_w || ' -1)';
  elsif ie_restricao_p = 'S' then
    ie_restricao_status_w := ' and a.ie_status_agenda in( ';
    FOR i IN (SELECT vl_dominio
              FROM valor_dominio_v
              WHERE cd_dominio = 83
              AND vl_dominio NOT IN ('L', 'B', 'R', 'LF', 'PA', coalesce(ie_executado_p, 'X'), coalesce(ie_canceladas_p,'X'))) LOOP
      ie_restricao_status_w := ie_restricao_status_w || '''' || i.vl_dominio || '''' || ',';
    END LOOP;

    ds_retorno_w := ie_restricao_status_w || '''' || '__'')';

  end if;

  return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
