SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_paciente_titulo (nr_titulo_p bigint, ie_codigo_nome_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255)	:= null;
nr_atendimento_w	bigint;
cd_pessoa_fisica_w	varchar(10);
nm_paciente_w		varchar(80);


BEGIN 
 
if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then 
 
	select	max(nr_atendimento) 
	into STRICT	nr_atendimento_w 
	from	titulo_receber 
	where	nr_titulo	= nr_titulo_p;
 
	if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then 
 
		select	a.cd_pessoa_fisica, 
			substr(obter_nome_pf(b.cd_pessoa_fisica),1,80)	 
		into STRICT	cd_pessoa_fisica_w, 
			nm_paciente_w 
		from	pessoa_fisica b, 
			atendimento_paciente a 
		where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica 
		and	a.nr_atendimento	= nr_atendimento_w;	
 
		if (ie_codigo_nome_p = 'D') then 
			ds_retorno_w	:= cd_pessoa_fisica_w;
		else 
			ds_retorno_w	:= nm_paciente_w;
		end if;
	end if;
	 
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_paciente_titulo (nr_titulo_p bigint, ie_codigo_nome_p text) FROM PUBLIC;

