SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_padrao_elem_nut_pac ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE



ds_retorno_w		varchar(60);
ds_padrao_w			varchar(20);


BEGIN

select	max(replace(qt_min,'.',',')||' - '||replace(qt_max,'.',',')),
		max(ds_padrao)
into STRICT	ds_retorno_w,
		ds_padrao_w
from	nut_elemento
where	nr_sequencia	= nr_sequencia_p;

if (position(',' in ds_retorno_w) = 1) then
	ds_retorno_w	:= '0'||ds_retorno_w;
end if;

ds_retorno_w	:= replace(ds_retorno_w,' ,',' 0,');

if (ds_padrao_w IS NOT NULL AND ds_padrao_w::text <> '') or (ds_padrao_w <> '') then
	ds_retorno_w	:= ds_padrao_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
