CREATE OR REPLACE FUNCTION obter_padrao_protocolo (cd_pessoa_p text, cd_perfil_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_retorno_w		bigint;
cd_protocolo_w			bigint;
cd_tipo_protocolo_w		bigint;

c01 CURSOR FOR
SELECT	cd_protocolo,
		cd_tipo_protocolo
from	rep_protocolo_padrao
where	coalesce(cd_perfil,cd_perfil_p)	= cd_perfil_p
and		coalesce(cd_pessoa_fisica,cd_pessoa_p) = cd_pessoa_p
order by coalesce(cd_perfil,0),
		coalesce(cd_pessoa_fisica,0);


BEGIN

open C01;
loop
fetch C01 into
	cd_protocolo_w,
	cd_tipo_protocolo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	null;
end loop;
close C01;

if (ie_opcao_p = 'T') then
	nr_seq_retorno_w	:= cd_tipo_protocolo_w;
elsif (ie_opcao_p = 'P') then
	nr_seq_retorno_w	:= cd_protocolo_w;
end if;

return	nr_seq_retorno_w;

end;
$body$
