SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_padronizacao_item_oc ( cd_estabelecimento_p bigint, ie_tipo_ordem_p text, cd_material_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
L	- IE_LIBERADO
C	- IE_EXIGE_CENTRO_CUSTO*/
cd_grupo_material_w			smallint;
cd_subgrupo_material_w			smallint;
cd_classe_material_w			integer;
ie_liberado_w				varchar(1) := 'S';
ie_exige_centro_custo_w			varchar(1) := 'N';
ds_retorno_w				varchar(1);

c01 CURSOR FOR
SELECT	coalesce(ie_liberado,'S'),
	coalesce(ie_exige_centro_custo,'N')
from	regra_padronizacao_oc a,
	regra_padronizacao_oc_item b
where	a.nr_sequencia = b.nr_seq_regra
and	a.ie_tipo_ordem = ie_tipo_ordem_p
and	a.cd_estabelecimento = cd_estabelecimento_p
and	coalesce(b.cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(b.cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(b.cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
and (coalesce(b.cd_material, cd_material_p) 			= cd_material_p or cd_material_p = 0)
order by
	coalesce(cd_material, 0),
	coalesce(cd_classe_material, 0),
	coalesce(cd_subgrupo_material, 0),
	coalesce(cd_grupo_material, 0);


BEGIN

select	a.cd_grupo_material,
	a.cd_subgrupo_material,
	a.cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v a
where	a.cd_material = cd_material_p;

open C01;
loop
fetch C01 into
	ie_liberado_w,
	ie_exige_centro_custo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_liberado_w				:= ie_liberado_w;
	ie_exige_centro_custo_w			:= ie_exige_centro_custo_w;
	end;
end loop;
close C01;

if (ie_opcao_p = 'L') then
	ds_retorno_w := ie_liberado_w;
elsif (ie_opcao_p = 'C') then
	ds_retorno_w := ie_exige_centro_custo_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
