CREATE OR REPLACE FUNCTION obter_pagador_atend (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE



ds_pagador_w		varchar(255);


BEGIN

select	substr(max(obter_nome_pf_pj(cd_pessoa_fisica, cd_cgc)),1,255)
into STRICT	ds_pagador_w
from 	atendimento_pagador
where	nr_atendimento		= nr_atendimento_p;

return	ds_pagador_w;


end;
$body$
LANGUAGE PLPGSQL
