CREATE OR REPLACE FUNCTION obter_pagina_livro ( nr_pagina_atual_p bigint, nr_inicial_p bigint, nr_total_p bigint) RETURNS bigint AS $body$
DECLARE


nr_retorno_w		bigint;
nr_contador_w		bigint	:= 0;
nr_resto_w		bigint	:= 0;


BEGIN
nr_resto_w		:= nr_pagina_atual_p;

while(nr_resto_w > 0) loop
	begin
	nr_contador_w	:= nr_contador_w + 1;

	nr_resto_w	:= nr_resto_w - nr_total_p;

	if (nr_resto_w > 0) then
		nr_contador_w	:= nr_contador_w + 1;
	end if;
	end;
end loop;

nr_retorno_w	:= nr_pagina_atual_p + nr_contador_w + nr_inicial_p;

return nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
