CREATE OR REPLACE FUNCTION obter_pagina_relat ( nr_linha_p bigint, nr_registros_pagina_p bigint) RETURNS bigint AS $body$
DECLARE

qt_retorno_w	bigint;
nr_multiplo_w		double precision	:= 0;


BEGIN



if	((trunc(nr_linha_p / nr_registros_pagina_p) * nr_registros_pagina_p) = nr_linha_p) then
	nr_multiplo_w	:= (nr_linha_p / nr_registros_pagina_p);
else
	nr_multiplo_w   := trunc(nr_linha_p / nr_registros_pagina_p) + 1;
end if;
qt_retorno_w	:= nr_registros_pagina_p * nr_multiplo_w / nr_registros_pagina_p;


return	qt_retorno_w;

end;
$body$
