CREATE OR REPLACE FUNCTION obter_painel_dinamico (nr_seq_painel_p bigint) RETURNS varchar AS $body$
DECLARE

 
 
nr_seq_painel_w		 	bigint;
nr_seq_painel_padrao_w	bigint;
cd_perfil_w			 	bigint;
cd_estabelecimento_w 	bigint;
cd_pessoa_fisica_w	 	varchar(10);
ds_retorno_w	 		varchar(1);
ds_padrao_w	 			varchar(1);


BEGIN 
 
 
cd_perfil_w		:= obter_perfil_ativo;
cd_estabelecimento_w	:= coalesce(obter_estabelecimento_ativo,0);
cd_pessoa_fisica_w	:= obter_pessoa_fisica_usuario(wheb_usuario_pck.get_nm_usuario,'C');
 
select	max(coalesce('S','N')) 
into STRICT 	ds_padrao_w 
from	painel_pa_sql_liberacao 
where	nr_seq_painel = nr_seq_painel_p 
and		ie_situacao	= 'A';
 
if (ds_padrao_w = 'S')then 
 
	select	max(coalesce('S','N')) 
	into STRICT 	ds_retorno_w 
	from	painel_pa_sql_liberacao 
	where	nr_seq_painel = nr_seq_painel_p 
	and   coalesce(cd_pessoa_fisica,	coalesce(cd_pessoa_fisica_w,0))			= coalesce(cd_pessoa_fisica_w,0) 
	and		coalesce(cd_perfil, 			coalesce(cd_perfil_w,0)) 				= coalesce(cd_perfil_w,0) 
	and		coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_w,0))		= coalesce(cd_estabelecimento_w,0) 
	and		ie_situacao	= 'A';
	 
	return ds_retorno_w;
end if;
 
return 'S';
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
