CREATE OR REPLACE FUNCTION obter_pais_sistema ( cd_perfil_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_retorno_w	integer;
ds_pais_w	varchar(15);


BEGIN


ds_pais_w := coalesce(pkg_i18n.get_user_locale, 'pt_BR');

select	max(coalesce(nr_sequencia, 0))
into STRICT 	nr_retorno_w
from 	tasy_idioma
where 	ds_language_tag = ds_pais_w;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
